import { useState } from "react";
import axios from "axios";

const News = () => {
    const [topic, setTopic] = useState("");
    const [newsData, setNewsData] = useState(null);
    const [error, setError] = useState("");  // ✅ Para mostrar errores

    const searchNews = async (topic) => {
        try {
            const response = await axios.get(`/api/news/${topic}`);
            setNews(response.data);
        } catch (error) {
            if (error.response && error.response.status === 404) {
                alert('No se encontraron noticias para este tema.');
            } else {
                alert('Hubo un problema al buscar las noticias.');
            }
        }
    };
    
    return (
        <div>
            <h2>Buscar Noticias</h2>
            <input 
                type="text" 
                value={topic} 
                onChange={(e) => setTopic(e.target.value)} 
                placeholder="Ingresa un tema (ej. tecnología)"
            />
            <button onClick={searchNews}>Buscar</button>

            {error && <p style={{ color: "red" }}>{error}</p>}  {/* ✅ Muestra error */}

            {newsData && newsData.map((news, index) => (
                <div key={index}>
                    <h3>{news.title}</h3>
                    <p>{news.description}</p>
                    <a href={news.url} target="_blank" rel="noopener noreferrer">Leer más</a>
                </div>
            ))}
        </div>
    );
};

export default News;
