import { useState } from "react";
import axios from "axios";

const Movies = () => {
    const [title, setTitle] = useState("");  // ✅ Asegurar que 'title' está definido
    const [movieData, setMovieData] = useState(null);

    const searchMovie = async () => {
        try {
            const response = await axios.get(`http://localhost:4000/api/movie/${title}`);
            setMovieData(response.data);
        } catch (error) {
            console.error("Error en el frontend:", error);
        }
    };

    return (
        <div>
            <h2>Buscar Película</h2>
            <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)}  // ✅ Actualiza 'title'
                placeholder="Ingresa el título"
            />
            <button onClick={searchMovie}>Buscar</button>

            {movieData && (
                <div>
                    <h3>{movieData.Title}</h3>
                    <p>{movieData.Plot}</p>
                    <img src={movieData.Poster} alt={movieData.Title} />
                </div>
            )}
        </div>
    );
};

export default Movies;
