import { useState } from 'react';
import axios from 'axios';

const Movies = () => {
    const [movie, setMovie] = useState('');
    const [data, setData] = useState(null);

    const searchMovie = async () => {
        const response = await axios.get(`http://localhost:4000/api/movie/${movie}`);
        setData(response.data);
    };

    return (
        <div>
            <h2>Buscar Pel√≠cula</h2>
            <input type="text" value={movie} onChange={e => setMovie(e.target.value)} />
            <button onClick={searchMovie}>Buscar</button>
            {data && <p>{data.Title} ({data.Year})</p>}
        </div>
    );
};

export default Movies;
