import React, { useState } from 'react';
import axios from 'axios';

const Pokemon = () => {
    const [pokemonName, setPokemonName] = useState('');
    const [pokemon, setPokemon] = useState(null);
    const [error, setError] = useState('');

    const searchPokemon = async () => {
        try {
            // Realiza la solicitud al backend para obtener los datos del Pokémon
            const response = await axios.get(`http://localhost:4000/api/pokemon/${pokemonName}`);
            setPokemon(response.data);  // Guarda la respuesta con el Pokémon
            setError('');
        } catch (error) {
            setError('Pokémon no encontrado');
            setPokemon(null);  // Limpiar el estado si hay error
        }
    };

    return (
        <div>
            <h1>Buscar Pokémon</h1>
            <input
                type="text"
                value={pokemonName}
                onChange={(e) => setPokemonName(e.target.value)}  // Actualiza el nombre del Pokémon
                placeholder="Ingrese el nombre del Pokémon"
            />
            <button onClick={searchPokemon}>Buscar Pokémon</button>

            {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Muestra el error si no se encuentra */}

            {pokemon && (
                <div>
                    <h2>{pokemon.name}</h2>
                    {/* Aquí no se muestra la imagen */}
                    <p><strong>Tipo:</strong> {pokemon.type}</p>
                </div>
            )}
        </div>
    );
};

export default Pokemon;
