import React, { useState } from 'react';
import axios from 'axios';

const Pokemon = () => {
    const [pokemonName, setPokemonName] = useState('');
    const [pokemon, setPokemon] = useState(null);
    const [error, setError] = useState('');

    const searchPokemon = async () => {
        try {
            const response = await axios.get(`/api/pokemon/${pokemonName}`);
            setPokemon(response.data);  // Guarda la respuesta con el Pokémon
            setError('');
        } catch (error) {
            setError('Pokémon no encontrado');
            setPokemon(null);  // Limpiar el estado si hay error
        }
    };

    return (
        <div>
            <input
                type="text"
                value={pokemonName}
                onChange={(e) => setPokemonName(e.target.value)}
                placeholder="Ingrese el nombre del Pokémon"
            />
            <button onClick={searchPokemon}>Buscar Pokémon</button>

            {error && <p>{error}</p>}

            {pokemon && (
                <div>
                    <h2>{pokemon.name}</h2>
                    <img src={pokemon.image} alt={pokemon.name} />
                    <p>Tipo: {pokemon.type}</p>
                </div>
            )}
        </div>
    );
};

export default Pokemon;
